import java.util.Scanner;

public class Array_Finding_EvenNumber_From_Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		Scanner input = new Scanner(System.in);
		
		
		System.out.println(" Please input 3 numbers for array to store in array");
		
		int array_of_3_Numbers[]= {input.nextInt(),  input.nextInt() ,input.nextInt()  };
		
		for (int i = 0; i < array_of_3_Numbers.length; i++) {
			
			System.out.println();
			
			if( array_of_3_Numbers[i] %2 ==0 ) 
			{
				System.out.println(array_of_3_Numbers[i]+"  is even");
			}
			
			else
			{
				System.out.println(array_of_3_Numbers[i]+" is odd");
			}
			
			
		}
	
	}

}
