
public class Constructors_In_Java_Practice {

	int x;
	
	public Constructors_In_Java_Practice(){
		// constructors are only used to initialized the values 
		
		x=5;
	}
	
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Constructors_In_Java_Practice obj = new Constructors_In_Java_Practice();
		
		
		System.out.println(obj.x);
		
		
	}

}


