
public class Even_for_you {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//print 1 to 100 even numbers
		
		for(int i=1;i<=100;i++) {
			
			if(i%2 == 0) {    System.out.println("Even Numbers :"+i);        }
		}
		
		//reverse mode
		
		
		// remember if you dont change the grater than sign into less than 
		//sign this loop wont work 
		
for(int j=100;j>=1;j--) {
			
			if(j%2 == 0) {    System.out.println("Reverse Even Numbers :"+j);        }
		}



// Odd
for(int k=1;k<=100;k++) {
	
	if(k%2 == 1) {    System.out.println(" Odd Numbers :"+k);        }
}


//reverse Odd Mode
for(int l=100;l>=1;l--) {
	
	if(l%2 == 1) {    System.out.println("Reverse Odd Numbers :"+l);        }
}





		
	}

}
