package forLoop;

public class For1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// for loop

		/*
		
		for(int i=1;i<10;i++) {
			
			System.out.println("hello");
		}
		*/
		
		//reverse mode
		/*starting point becomes ending point ,ending points become starting point ,
		less than becomes grater than , increment becomes decrement */
		
		for(int i=10 ; i>=0;i--) {
			
			System.out.println("reverse number are : "+i);
		}
		
		
		
	}

}
